    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // obtener los datos de  la interfaz:
        String moneda=getMoneda();
        String bien=getBien();
        double tasa= getTasa();
        double inicial= getIncial();
        double valor=getvalor();
        int meses=getMeses();     
        
        
        //realizar los calculos
        double cuotaInicial=calcularCuotaInicial(valor);
        double riesgo=calcularRiesgo(valor,cuotaInicial);
        double cuotaMensual=calculoCuotaMensual(riesgo,meses);
        
       
        //enviar los datos ala interfaz
        jLabel7.setText("$"+cuotaInicial);
        jLabel11.setText("$"+riesgo);
        jLabel9.setText("$"+cuotaMensual);
    }//GEN-LAST:event_jButton1ActionPerformed

