    private void llenarMoneda() {
        jComboBox1.addItem("SELECCIONE UNO....");
        jComboBox1.addItem("PESOS");
        jComboBox1.addItem("DOLARES");
    }

    private String getMoneda() {
        return String.valueOf(jComboBox1.getSelectedItem());
    }
    private String getBien() {
    return String.valueOf(jComboBox2.getSelectedItem());
    }

    private double getTasa() {
    return Double.parseDouble(jLabel13.getText());
    }

    private double getIncial() {
    return Double.parseDouble(jLabel15.getText());
    }

    private double getvalor() {
   return Double.parseDouble(jTextField2.getText());
    }

    private int getMeses() {
   return Integer.parseInt(jTextField1.getText());
    }

    private double calcularCuotaInicial(double valor) {
    return valor*0.2;
    }

    private double calcularRiesgo(double valor, double cuotaInicial) {
   return valor-cuotaInicial;
    }

    private double calculoCuotaMensual(double riesgo, int meses) {
   return (riesgo*1.12)/meses;
    }
